package assignment1;

public class SevenProgram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		System.out.println(a++ + --a   - --a  + ++a);
		System.out.println(a++);
		System.out.println(--a );
		System.out.println(--a);
		System.out.println(++a);
		
		//10 + 10 - 9 + 10
		//30-9
		//output = 21

	}

}
